from torchvision import transformsimport torchvision.transforms as Tmean = [0.485, 0.456, 0.406]std = [0.229, 0.224, 0.225]train_transform = transforms.Compose([    transforms.RandomResizedCrop(224),    transforms.RandomHorizontalFlip(),    transforms.ColorJitter(brightness=0.3, contrast=0.3, saturation=0.3),    transforms.RandomRotation(15),    transforms.ToTensor(),    transforms.Normalize(mean, std),])val_transform = transforms.Compose([    transforms.Resize((224, 224)),    transforms.ToTensor(),    transforms.Normalize(mean, std),])test_transform = T.Compose([    T.Resize((224, 224)),    T.ToTensor(),    T.Normalize([0.485, 0.456, 0.406], [0.229, 0.224, 0.225])])